import { ObjectId } from "bson"

let users
const DEFAULT_SORT = [["tomatoes.viewer.numReviews", -1]]

export default class usersDAO {
  static async injectDB(conn) {
    if (users) {
      return
    }
    try {
     // mflix = await conn.db(process.env.MFLIX_NS)
      users = await conn.db(process.env.FOOTBALL_NS).collection("users")
      this.users = users // this is only for testing
    } catch (e) {
      console.error(
        `Unable to establish a collection handle in usersDAO: ${e}`,
      )
    }
  }
  /**
   * Finds and returns all users .
   * Returns a list of objects, each object contains a user_id, name and club.
   * @param {Object} filters - The search parameters to use in the query.
   * @param {number} page - The page of users to retrieve.
   * @param {number} usersPerPage - The number of users to display per page.
   * @returns {GetUsersResult} An object with user results and total results
   * that would match this query
   */
   static async getUsers({
    // here's where the default parameters are set for the getUsers method
    filters = null,
    page = 0,
    usersPerPage = 10,
  } = {}) {
    let queryParams = {}
    if (filters) {
     /* if ("text" in filters) {
        queryParams = this.textSearchQuery(filters["text"])
      } else if ("cast" in filters) {
        queryParams = this.castSearchQuery(filters["cast"])
      } else if ("genre" in filters) {
        queryParams = this.genreSearchQuery(filters["genre"])
      }*/
    }

    let { query = {}, project = {}, sort = DEFAULT_SORT } = queryParams
    let cursor
    try {
      cursor = await users
        .find(query)
        .project(project)
        .sort(sort)
    } catch (e) {
      console.error(`Unable to issue find command, ${e}`)
      return { usersList: [], totalNumUsers: 0 }
    }

    /**
    Ticket: Paging

    Before this method returns back to the API, use the "usersPerPage" and
    "page" arguments to decide the users to display.

    Paging can be implemented by using the skip() and limit() cursor methods.
    */

    // Use the cursor to only return the users that belong on the current page
    const displayCursor = cursor.limit(usersPerPage)

    try {
      const usersList = await displayCursor.toArray()
      const totalNumUsers = page === 0 ? await users.countDocuments(query) : 0

      return { usersList, totalNumUsers }
    } catch (e) {
      console.error(
        `Unable to convert cursor to array or problem counting documents, ${e}`,
      )
      return { usersList: [], totalNumUsers: 0 }
    }
  }
}

/**
 * Result set for getMovies method
 * @typedef GetUsersResult
 * @property {MflixMovies[]} usersList
 * @property {number} totalNumUsers
 */

