sourcesContent:
import UsersDAO from "../dao/usersDAO"

export default class UsersController {
  static async apiGetUsers(req, res, next) {
    const USERS_PER_PAGE = 10
    const { usersList, totalNumUsers } = await UsersDAO.getUsers()
    let response = {
      users: usersList,
      page: 0,
      filters: {},
      entries_per_page: USERS_PER_PAGE,
      total_results: totalNumUsers,
    }
    res.json(response)
  }

  static async apiGetUsersByIds(req, res, next) {
    let ids = req.query.ids == "" ? "" : req.body.user_ids
    let idsList = Array.isArray(ids) ? ids : Array(ids)
    let usersList = await UsersDAO.getUsersByIds(idsList)
    let response = {
      titles: usersList,
    }
    res.json(response)
  }

  static async apiGetUsersById(req, res, next) {
    try {
      let id = req.body.user_id || {}
      let user = await UsersDAO.getUserByID(id)
      if (!user) {
        res.status(404).json({ error: "Not found" })
        return
      }
      let updated_type = user.lastupdated instanceof Date ? "Date" : "other"
      res.json({ user, updated_type })
    } catch (e) {
      console.log(`api, ${e}`)
      res.status(500).json({ error: e })
    }
  }

  static async apiGetNestedDocsByID(req, res, next) {
    try {
      let ids = req.query.ids == "" ? "" : req.body.user_ids
      let idsList = Array.isArray(ids) ? ids : Array(ids)
      let usersList = await UsersDAO.getNestedDocsByID(idsList)
      //console.log(usersList)
      let response = usersList
     
    res.json(response)
    } catch (e) {
      console.log(`api, ${e}`)
      res.status(500).json({ error: e })
    }
  }
}

